mapper.update:
/* sql : codeRepository.update */
UPDATE  TB_CODE
SET     UPDATED_AT = now()
        , UPDATED_ID = #{updated_id}
        , MEMO = #{memo}
        , ORD = #{ord}
        , CODE_NM = #{code_nm}
        , USE_YN = #{use_yn}
WHERE   CODE_ID = #{code_id}

mapper.delete:
/* sql : codeRepository.deleteByCodeId */
DELETE
FROM TB_CODE
WHERE CODE_ID = #{code_id}
