mapper.select:
/* sql : codeGrpRepository.findAll */ SELECT ROW_NUMBER() OVER (ORDER BY CODE_GRP_ID DESC) RNUM, CODE_GRP_ID, CODE_GRP_NM, CODE_GRP_VAL, CREATED_AT, CREATED_ID, UPDATED_AT, UPDATED_ID, USE_YN FROM TB_CODE_GRP WHERE 1=1
AND USE_YN = #{use_yn}
AND (UPPER(CODE_GRP_NM) LIKE '%'||UPPER(#{search_str})||'%' OR UPPER(CODE_GRP_VAL) LIKE '%'||UPPER(#{search_str})||'%')
ORDER BY CODE_GRP_ID DESC OFFSET #{start_idx} limit #{page_per};

/* sql : codeGrpRepository.countAll */ SELECT COUNT(*) FROM TB_CODE_GRP WHERE 1=1
AND USE_YN = #{use_yn}
AND (UPPER(CODE_GRP_NM) LIKE '%'||UPPER(#{search_str})||'%' OR UPPER(CODE_GRP_VAL) LIKE '%'||UPPER(#{search_str})||'%')

/* sql : codeGrpRepository.countByCodeGrpVal */
SELECT  COUNT(*)
FROM    TB_CODE_GRP
WHERE   CODE_GRP_VAL = #{code_grp_val}

mapper.update:
/* sql : codeGrpRepository.update */
UPDATE  TB_CODE_GRP
SET     UPDATED_AT = now(),
        UPDATED_ID = #{updated_id},
        CODE_GRP_NM = #{code_grp_nm},
        USE_YN = #{use_yn}
WHERE   CODE_GRP_ID = #{code_grp_id}
