mapper.select:
/* sql : FaqRepository.findAll */ SELECT ROW_NUMBER() OVER (ORDER BY A.FAQ_ID DESC) RNUM, A.FAQ_ID, A.BOARD_ID, A.FAQ_TITLE, A.CREATED_AT, TO_CHAR(A.CREATED_AT, 'YYYY-MM-DD HH24:MI') AS CREATED_AT, TO_CHAR(A.UPDATED_AT, 'YYYY-MM-DD HH24:MI') AS UPDATED_AT, FN_GETUSERNM(A.CREATED_ID) AS CREATED_NM, FN_GETUSERNM(A.UPDATED_ID) AS UPDATED_NM, A.CREATED_ID, A.UPDATED_ID, A.USE_YN, FN_GETCODENM('USE_YN', A.USE_YN) AS USE_YN_NM, B.BOARD_TITLE, A.VIEW_CNT FROM TB_FAQ A, TB_BOARD B WHERE A.BOARD_ID = B.BOARD_ID AND B.USE_YN = 'Y'
AND A.USE_YN = #{use_yn}
AND B.BOARD_ID = #{board_id}
AND UPPER(A.FAQ_TITLE) LIKE '%'||UPPER(#{faq_title})||'%'
OFFSET #{start_idx} limit #{page_per}

/* sql : FaqRepository.countAll */
SELECT  COUNT(*)
FROM    TB_FAQ A,
        TB_BOARD B
WHERE   A.BOARD_ID  = B.BOARD_ID
AND     B.USE_YN = 'Y'

AND     A.USE_YN = #{use_yn}


AND     B.BOARD_ID = #{board_id}


AND     UPPER(A.FAQ_TITLE) LIKE  '%'||UPPER(#{search_str})||'%'

mapper.update:
/* sql : FaqRepository.update */
UPDATE  TB_FAQ
SET     UPDATED_AT = now()

        , UPDATED_ID = #{updated_id}


        , FAQ_TITLE = #{faq_title}


        , MAIN_TEXT = #{main_text}


        , USE_YN = #{use_yn}


        , VIEW_CNT = COALESCE(VIEW_CNT, 0) + 1

WHERE   FAQ_ID = #{faq_id}
