mapper.select:
/* sql : PostRepository.findAll */ SELECT ROW_NUMBER() OVER (ORDER BY A.POST_ID DESC) RNUM, A.POST_ID, A.BOARD_ID, A.POST_TITLE, A.CREATED_ID, A.USE_YN, FN_GETCODENM('USE_YN', A.USE_YN) AS USE_YN_NM, B.BOARD_TITLE, TO_CHAR(A.CREATED_AT, 'YYYY-MM-DD HH24:MI') AS CREATED_AT, FN_GETUSERNM(A.CREATED_ID) AS CREATED_NM, FN_GETCODENM('BOARD_TYPE', B.BOARD_TYPE) AS BOARD_TYPE_NM, A.VIEW_CNT FROM TB_POST A, TB_BOARD B WHERE A.BOARD_ID = B.BOARD_ID AND B.USE_YN = 'Y'
AND A.USE_YN = #{use_yn}
AND B.BOARD_ID = #{board_id}
AND UPPER(A.POST_TITLE) LIKE '%'||UPPER(#{search_str})||'%'
AND TO_CHAR(A.CREATED_AT, 'YYYY-MM-DD') BETWEEN #{start_date} AND #{end_date}
OFFSET #{start_idx} limit #{page_per}

/* sql : PostRepository.countAll */
SELECT  COUNT(*)
FROM    TB_POST A,
        TB_BOARD B
WHERE   A.BOARD_ID  = B.BOARD_ID
AND     B.USE_YN = 'Y'

AND     A.USE_YN = #{use_yn}


AND     B.BOARD_ID = #{board_id}


AND     UPPER(A.POST_TITLE) LIKE  '%'||UPPER(#{post_title})||'%'


AND     TO_CHAR(A.CREATED_AT, 'YYYY-MM-DD') BETWEEN #{start_date} AND #{end_date}

mapper.update:
/* sql : PostRepository.update */
UPDATE  TB_POST
SET UPDATED_AT = now()

    , UPDATED_ID = #{updated_id}


    , POST_TITLE = #{post_title}


    , MAIN_TEXT = #{main_text}


    , USE_YN = #{use_yn}


    , VIEW_CNT = COALESCE(VIEW_CNT, 0) + 1

WHERE POST_ID = #{post_id}
