mapper.select:
/* sql : UserRepository.countByLoginId */
SELECT  COUNT(*)
FROM 	TB_USER
WHERE	LOGIN_ID = #{login_id}

mapper.update:
UPDATE TB_USER
SET  UPDATED_AT = now()
,UPDATED_ID = #{updated_id}

    , USER_NM = #{user_nm}


    , USER_PW = #{user_pw}


    , TEL_NO = #{tel_no}


    , PW_INIT_YN = #{pw_init_yn}

WHERE USER_ID = #{user_id}

mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY USER_ID DESC) RNUM, USER_ID, LOGIN_ID, USER_NM, USER_PW, TEL_NO, USE_YN, FN_GETCODENM('USE_YN', USE_YN) AS USE_YN_NM, CREATED_AT, UPDATED_AT, CREATED_ID, UPDATED_ID FROM TB_USER WHERE 1=1
AND (UPPER(LOGIN_ID) LIKE '%'||UPPER(#{search_str})||'%' OR UPPER(USER_NM) LIKE '%'||UPPER(#{search_str})||'%')
AND USE_YN = #{use_yn}
ORDER BY USER_ID DESC OFFSET #{start_idx} limit #{page_per}

SELECT COUNT(*) FROM TB_USER WHERE 1=1
AND (UPPER(LOGIN_ID) LIKE '%'||UPPER(#{search_str})||'%' OR UPPER(USER_NM) LIKE '%'||UPPER(#{search_str})||'%')
AND USE_YN = #{use_yn}
